$(document).ready(function(){

    // Show login form
    $("#openInicio").click(function(){
        $("#inicioPopUp").css("display", "block");
    });

    // Close login form
    $("#closeInicio").click(function(){
        $("#inicioPopUp").css("display", "none");
    });

    // Show registration form
    $("#openRegistro").click(function(){
        $("#registroPopUp").css("display", "block");
    });

    // Close registration form
    $("#closeRegistro").click(function(){
        $("#registroPopUp").css("display", "none");
    });

});